import styled from 'styled-components';

export const ProductListStyles = styled.section`

    .product{
      margin-left: 5%;
      width: 90%;
      height: 30px;
      border-botomm: 1px solid white;
    }

    .product-field{
      float:left;
      width: 15%;
    }

    .product-controls{
      width: 30%;
    }

    .buttons {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 20px;
    }

    button{
      float: left;
      width : 30px;
      height: 20px;
    }
`
