export interface Product {
  id: number;
  name: string;
  customerPrice: number;
  cost : number;
}

export interface ItemProduct {
  id: number;
  name: string;
  customerPrice: number;
  cost : number;
  qty : number;
}

export interface ProductsListDomain {
  elements: Array<Product>;
  cart: Array<ItemProduct>;
  loading: boolean;
  error: boolean;
}

export const initialState: ProductsListDomain = {
  elements: [],
  cart: [],
  loading: false,
  error: false
};
