import React, {useCallback, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import Button from '../../components/Button';
import {counterLoadingSelector, counterSelector} from '../../store/modules/counter/selectors';
import {productsLoadingSelector, productsSelector, cartSelector} from '../../store/modules/products/selectors';
import {fetchProductsListRequestAction,decrementCounterAction, incrementCounterAction} from '../../store/modules/products/actions';
import {ProductListStyles} from './ProductsListStyles';

export const Counter: React.FC = () => {

    const dispatch = useDispatch();
    const elementsList = useSelector(productsSelector);
    const cartList = useSelector(cartSelector);
    const isLoading = useSelector(counterLoadingSelector);
    let qtyInCart;   

    const handleLoadClick = useCallback(() => {
        console.log(`loaded`);
        fetchProductsList();
    }, [dispatch]);

    const handleIncrementClick = useCallback((_id) => {
        dispatch(incrementCounterAction(_id));
    }, [dispatch]);

    const handleDecrementClick = useCallback((_id) => {
        dispatch(decrementCounterAction(_id));
    }, [dispatch]);

    // Will trigger cart modal
    const handleCartClick = useCallback(() => {
        //dispatch(...);
    }, [dispatch]);

    const fetchProductsList = useCallback(() => {
        console.log(`loaded1`);
        dispatch(fetchProductsListRequestAction());
    }, [dispatch]);

    useEffect( () => {    
        qtyInCart = cartList.reduce( (acc, value) => acc + value.qty , 0);
    });

    const productsSelectorsDOM = elementsList.map( e => (
        <div className="product">
            <div className="product-name product-field">{e.name}</div>
            <div className="product-customerPrice product-field">{e.customerPrice}</div>
            <div className="product-controls product-field">
                <div className="buttons">
                    <Button onClick={() => handleIncrementClick(e.id) } disabled={isLoading}>+</Button>
                    <Button onClick={() => handleDecrementClick(e.id)} disabled={isLoading}>-</Button>
                </div>
            </div>
        </div>
    ) );

    return (
        <ProductListStyles>
            <div className="productsContainer">
                <h1>Catalog</h1>
                <div className="loadContainer">
                    <Button onClick={handleLoadClick} disabled={isLoading}>Load</Button>
                </div>
                <div className="cartViewContainer">
                    <Button onClick={handleCartClick} disabled={isLoading}>Cart ({qtyInCart})</Button>
                </div>
                <div className="productsListContainer">{productsSelectorsDOM}</div>                
            </div>
        </ProductListStyles>
    )
};

export default Counter;
