import styled from 'styled-components';

export const ProductListStyles = styled.section`

    .product{
      margin-left: 5%;
      width: 90%;
      border-botomm: 1pz solid white;
    }

    . product-field{
      float:left;
    }

    .currentCounterValue {
        color: red;
    }

    .buttons {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 20px;
    }
`
