import { all, spawn, call } from 'redux-saga/effects';
import { watchIncrementCounterSaga } from './modules/counter/saga';
import { watchFetchProductSaga,watchShowCartSaga } from './modules/products/saga';

export default function* allSagas() {
  const sagas = [
      watchIncrementCounterSaga,
      watchFetchProductSaga,
      watchShowCartSaga
  ];

  yield all(
    sagas.map(saga =>
      spawn(function*() {
        while (true) {
          try {
            yield call(saga);
            break;
          } catch (e) {
            console.error(`Error ${e}`);
          }
        }
      })
    )
  );
}
