import styled from 'styled-components';

export const ProductListStyles = styled.section`
    display: flex;
    flex-direction: column;
    flex-grow: 1;
    align-self: center;
    padding-top: 22px;

    .product{
      margin-left: 5%;
      width: 90%;
      border-botomm: 1pz solid white;
    }

    . product-field{
      float:left;
    }

    .currentCounterValue {
        color: red;
    }

    .buttons {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 20px;
    }
`
