import styled from 'styled-components';

export const ProductListStyles = styled.section`

    .productsContainer{
      width: 100%;
      height: 100%;
    }

    .productsListContainer{
      width: 100%;
      padding-top: 50px;
      overflow: auto;
    }

    .loadContainer{
      position: absolute;
      top: 30px;
      left: 30px;
      width : 200px;
      height: 50px;
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .loadContainer button{
      width : 300px;
      height: 40px;
      cursor: pointer;
    }

    .cartViewContainer{
      position: absolute;
      top: 30px;
      right: 30px;
      width : 100px;
      height: 50px;
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .cartViewContainer button{
      width : 150px;
      height: 40px;
      cursor: pointer;
    }

    .product{
      clear:both;
      margin-left: 5%;
      width: 90%;
      height: 30px;
      border-botomm: 1px solid white;
    }

    .product-field{
      float:left;
      width: 15%;
    }

    .product-controls{
      width: 30%;
    }

    .buttons {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 20px;
    }

    button{
      float: left;
      width : 30px;
      height: 20px;
      cursor: pointer;
    }
`
