import React, {useCallback, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import Button from '../../components/Button';
import {counterLoadingSelector, counterSelector} from '../../store/modules/counter/selectors';
import {productsLoadingSelector, productsSelector} from '../../store/modules/products/selectors';
import {decrementCounterAction, incrementCounterRequestAction} from '../../store/modules/counter/actions';
import {fetchProductsListRequestAction} from '../../store/modules/products/actions';
import {ProductListStyles} from './ProductsListStyles';

export const Counter: React.FC = () => {
    const dispatch = useDispatch();
    const elementsList = useSelector(productsSelector);
    const isLoadingElements = useSelector(productsLoadingSelector);
    const counterValue = useSelector(counterSelector);
    const isLoading = useSelector(counterLoadingSelector);




    const handleIncrementClick = useCallback(() => {
        dispatch(incrementCounterRequestAction());
    }, [dispatch]);

    const handleDecrementClick = useCallback(() => {
        dispatch(decrementCounterAction());
    }, [dispatch]);

    const fetchProductsList = useCallback(() => {
        console.log(`loaded1`);
        dispatch(fetchProductsListRequestAction());
    }, [dispatch]);

    useEffect( () => {    
        console.log(`loaded`);
        fetchProductsList();
    });

    const signalsSelectorsDOM = elementsList.map( e => (
        <div className="product">
            <div className="product-name product-field">{e.name}</div>
            <div className="product-customerPrice product-field">{e.customerPrice}</div>
            <div className="product-controls product-field">
                <div className="buttons">
                    <Button onClick={handleIncrementClick} disabled={isLoading}>+</Button>
                    <Button onClick={handleDecrementClick} disabled={isLoading}>-</Button>
                </div>
            </div>
        </div>
    ) );

    return (
        <ProductListStyles>
            <div className="productsListContainer">
                <h1>Catalog</h1>
                <div>{signalsSelectorsDOM}</div>                
            </div>
        </ProductListStyles>
    )
};

export default Counter;
