import {ProductsListDomain} from './model';
import {createSelector} from 'reselect';

export const productsDomain = (state: any): ProductsListDomain => state.elements;

export const productsSelector = createSelector(
    productsDomain,
    function (domain: ProductsListDomain): ProductsListDomain['elements'] {
        return domain.elements;
    }
);

export const productsLoadingSelector = createSelector(
    productsDomain,
    (domain: ProductsListDomain): ProductsListDomain['loading'] => domain.loading
);
